mapper.insert:
INSERT INTO menu (
    parent_id
    , uri
    , node_path
    , `name`
    , `order`
    , icon_path
    , use_yn
    , roles
    , description
    , create_id
    , update_id
) VALUES (
    #{parentId}
    , #{uri}
    , #{nodePath}
    , #{name}
    , (SELECT COALESCE (MAX (`order`) + 1, 1) FROM menu WHERE parent_id = #{parentId})
    , #{iconPath}
    , #{useYn}
    , #{roles}
    , #{description}
    , #{createId}
    , #{updateId}
)

mapper.update:
UPDATE menu


        parent_id = #{parentId}


        , uri = #{uri}


        , node_path = #{nodePath}


        , `name` = #{name}



            #{order}


            , (SELECT `order` FROM
                (SELECT COALESCE(max(`order`) + 1, 1) `order` FROM `menu`
                WHERE parent_id = (SELECT parent_id FROM `menu` WHERE `id` = #{id})
            ) as `order`)



        , icon_path = #{iconPath}


        , use_yn = #{useYn}

    , roles = #{roles}
    , description = #{description}
    , update_id = #{updateId}
    , update_time = NOW()

WHERE id = #{id}
